    private void showtable(){
    try
        {
            con = DriverManager.getConnection("jdbc:oracle:thin:@localhost:1521:xe","system","dinesh07");
            String sql="select * from dcustomers";
            st=con.prepareStatement(sql);
            rs=st.executeQuery();
            DefaultTableModel model = (DefaultTableModel) custtable.getModel();
            model.setRowCount(0);
            // Add new data from the result set to the table
            while (rs.next()) {
                Object[] row = {
                        rs.getInt("cid"),
                        rs.getString("cname"),
                        rs.getString("phoneno"),
                };
                model.addRow(row);
            }
            custtable.getColumnModel().getColumn(1).setPreferredWidth(250);
            if (model.getRowCount() == 0) {
            JOptionPane.showMessageDialog(null, "No Customers Available");
        }
        }
        catch(HeadlessException | SQLException ex)
        {
            JOptionPane.showMessageDialog(null,ex); 
        }
    }
