    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        try
        {
            con = DriverManager.getConnection("jdbc:oracle:thin:@localhost:1521:xe","system","dinesh07");
            String sql="insert into dcustomers values(?,?,?)";
            st=con.prepareStatement(sql);
            st.setString(1, jTextField1.getText());        
            st.setString(2, jTextField2.getText());
            st.setString(3, jTextField3.getText());
            st.executeUpdate();
            JOptionPane.showMessageDialog(null,"Success");
            jTextField1.setText("");
            jTextField2.setText("");
            jTextField3.setText("");
            showtable();
        }
        catch(HeadlessException | SQLException ex)
        {
            JOptionPane.showMessageDialog(null,ex); 
        }
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        // TODO add your handling code here:
        try 
        {
            con = DriverManager.getConnection("jdbc:oracle:thin:@localhost:1521:xe", "system", "dinesh07");
            int option = JOptionPane.showConfirmDialog(this, "Are you sure you want to delete this Customer ?", "Confirmation", JOptionPane.YES_NO_OPTION);        
            if (option == JOptionPane.YES_OPTION)
            {
            String sql = "delete from dcustomers where cid=?";
            st = con.prepareStatement(sql);
            st.setString(1, jTextField1.getText());
            int row = st.executeUpdate();
            if (row>0) 
            {
                JOptionPane.showMessageDialog(null, "Customer details deleted successfully");
                jTextField1.setText("");
                jTextField2.setText("");
                jTextField3.setText("");
                showtable(); 
            }
            else 
            {
                JOptionPane.showMessageDialog(null, "Customer not found");
            }
            }
        } 
        catch (HeadlessException | SQLException ex) 
        {
            JOptionPane.showMessageDialog(null, ex);
        }
    }//GEN-LAST:event_jButton4ActionPerformed

